
public class stardp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//int k=0;
		for(int i=5;i>0;i--) {
			
			for(int k=i;k<5;k++) {
				System.out.print(' ');
			}
			
			for(int j=i;j>0;j--) {
				System.out.print('*');
			}
			System.out.println();
		}

	}

}
